import React, { Component } from "react";
import { Link } from "react-router-dom";
class Landing extends Component {
  render() {
    return (
      <div style={{ height: "75vh" }} className="container valign-wrapper">
        <div className="row">
          <div className="col s11 left-align">
            <h4>
              <b>Web Apps</b> Authentication Using MongoDB Atlas, ExpressJs, ReactJs and NodeJs {" "}
              <span style={{ fontFamily: "Source Sans Pro" }}>MERN</span> stack from
              scratch
            </h4>
            <p className="flow-text grey-text text-darken-1">
              Create a (minimal) full-stack app with user authentication via
              Passport and Jsonwebtoken
            </p>
            <br />
            <div className="col s0">
              <Link
                to="/register"
                style={{
                  width: "140px",
                  borderRadius: "5px",
                  letterSpacing: "1.5px",
                  
                }}
                className="btn btn-large waves-effect waves-light hoverable blue accent-3">
                Register
              </Link>
            </div>
            <div className="col s0">
              <Link
                to="/login"
                style={{
                  width: "140px",
                  borderRadius: "5px",
                  letterSpacing: "1.5px"
                }}
                className="btn btn-large waves-effect waves-light hoverable green accent-3"
              >
                Log In
              </Link>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Landing;
